package PracticePrograms;

public class swappingpractice {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int a =10;
int b=20;
int c;

System.out.println("Before Swapping "+a+"  "+b);

c=b;
b=a;
a=c;

System.out.println("After Swapping "+a+" "+b);
	}

}
